import React, { useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

// Set the app element for react-modal
Modal.setAppElement('#root');

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadResponse, setUploadResponse] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedPrinter, setSelectedPrinter] = useState(''); // State for selected printer

  // List of available printers
  const printers = [
    'Cơ sở 1 - Máy in A',
    'Cơ sở 1 - Máy in B',
    'Cơ sở 2 - Máy in C',
    'Cơ sở 2 - Máy in D'
  ];

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!selectedFile) {
      alert('Please select a file to upload!');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:3002/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setUploadResponse(response.data.message || 'File uploaded successfully!');
      setModalIsOpen(true);
    } catch (error) {
      console.error('Error uploading file:', error);
      setUploadResponse('There was an error uploading your file. Please try again.');
      setModalIsOpen(true);
    }
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="App">
      <h1>File Upload App</h1>
      <form onSubmit={handleUpload}>
        <div>
          <label>Select Printer:</label>
          <select value={selectedPrinter} onChange={(e) => setSelectedPrinter(e.target.value)}>
            <option value="">--Select a Printer--</option>
            {printers.map((printer, index) => (
              <option key={index} value={printer}>
                {printer}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Select File:</label>
          <input type="file" onChange={handleFileChange} />
        </div>
        <button type="submit">Upload File</button>
      </form>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} style={customStyles}>
        <h2>Upload Status</h2>
        <p>{uploadResponse}</p>
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
}

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
};

export default App;
