import React, { useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

// Set the app element for react-modal
Modal.setAppElement('#root');

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadResponse, setUploadResponse] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [printSettings, setPrintSettings] = useState({
    printer: '',
    printAllPages: false,
    paperSize: 'Letter',
    pages: '',
    printOneSided: true,
    normalMargins: true,
    orientation: 'Portrait',
    onePagePerSheet: true,
    copies: 1,
  });

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!selectedFile) {
      alert('Please select a file to upload!');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:3000/#Print', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const uploadedFileName = selectedFile.name;
      setUploadedFiles([...uploadedFiles, uploadedFileName]);
      setUploadResponse(response.data.message || 'File uploaded successfully!');
      setModalIsOpen(true);
    } catch (error) {
      console.error('Error uploading file:', error);
      setUploadResponse('There was an error uploading your file. Please try again.');
      setModalIsOpen(true);
    }
  };

  const handlePrintSettingChange = (e) => {
    const { name, value } = e.target;
    setPrintSettings((prevSettings) => ({
      ...prevSettings,
      [name]: value,
    }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setPrintSettings((prevSettings) => ({
      ...prevSettings,
      [name]: checked,
    }));
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="App">
      <form onSubmit={handleUpload}>
        <div>
          <label>Select File:</label>
          <input type="file" onChange={handleFileChange} />
        </div>
        <button type="submit">Tải tệp lên</button>
      </form>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} style={customStyles}>
        <h2>Upload Status</h2>
        <p>{uploadResponse}</p>
        <button onClick={closeModal}>Close</button>
      </Modal>
      <div>
        <h2>Các tệp tin đã tải</h2>
        <ul>
          {uploadedFiles.map((file, index) => (
            <li key={index}>{file}</li>
          ))}
        </ul>
      </div>

      {/* Print Settings Section */}
      <div>
        <h2>Settings</h2>
        <form>
          <div>
            <label>Printer:</label>
            <select name="printer" value={printSettings.printer} onChange={handlePrintSettingChange}>
              <option value="">Select Printer</option>
              <option value="Printer 1">Printer 1</option>
              <option value="Printer 2">Printer 2</option>
              <option value="Printer 3">Printer 3</option>
              {/* Add more printers as needed */}
            </select>
          </div>
          <div>
            <label>Print All Pages:</label>
            <input
              type="checkbox"
              name="printAllPages"
              checked={printSettings.printAllPages}
              onChange={handleCheckboxChange}
            />
          </div>
          <div>
            <label>Letter</label>
            <select name="paperSize" value={printSettings.paperSize} onChange={handlePrintSettingChange}>
              <option value="Letter">Letter (21.59 cm x 27.94 cm)</option>
              <option value="A4"></option>
              <option value="A3"></option>
              {/* Add other paper sizes as needed */}
            </select>
          </div>
          <div>
            <label>Pages:</label>
            <input
              type="text"
              name="pages"
              value={printSettings.pages}
              onChange={handlePrintSettingChange}
              placeholder="Enter specific pages"
            />
          </div>
          <div>
            <label>Print One Sided:</label>
            <input
              type="checkbox"
              name="printOneSided"
              checked={printSettings.printOneSided}
              onChange={handleCheckboxChange}
            />
          </div>
          <div>
            <label>Normal Margins:</label>
            <input
              type="checkbox"
              name="normalMargins"
              checked={printSettings.normalMargins}
              onChange={handleCheckboxChange}
            />
          </div>
          <div>
            <label>Orientation:</label>
            <select name="orientation" value={printSettings.orientation} onChange={handlePrintSettingChange}>
              <option value="Portrait">Portrait</option>
              <option value="Landscape">Landscape</option>
            </select>
          </div>
          <div>
            <label>1 Page Per Sheet:</label>
            <input
              type="checkbox"
              name="onePagePerSheet"
              checked={printSettings.onePagePerSheet}
              onChange={handleCheckboxChange}
            />
          </div>
          <div>
            <label>Copies:</label>
            <input
              type="number"
              name="copies"
              value={printSettings.copies}
              onChange={handlePrintSettingChange}
              min="1"
            />
          </div>
          <button type="submit">Print</button>
        </form>
      </div>
    </div>
  );
}

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
};

export default App;
