import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [printers, setPrinters] = useState([]);
  const [selectedPrinter, setSelectedPrinter] = useState("");
  const [file, setFile] = useState(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [config, setConfig] = useState({
    numCopies: 1,
    pageSize: "A4",
    isLandscape: false,
    isDoubleSided: false,
  });
  const [printerDetails, setPrinterDetails] = useState(null);
  const [showPrinterList, setShowPrinterList] = useState(false);

  // Fetch printer data
  useEffect(() => {
    fetch("http://localhost:3001/api/printers")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch printer data");
        }
        return response.json();
      })
      .then((data) => setPrinters(data))
      .catch((error) => console.error(error));
  }, []);

  // Handle file upload
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setShowConfirmation(true); // Show confirmation modal
  };

  // Handle cancel confirmation
  const handleCancelUpload = () => {
    setFile(null);
    setShowConfirmation(false);
  };

  // Handle submit confirmation
  const handleConfirmUpload = () => {
    setShowConfirmation(false);
  };

  // Handle printer selection
  const handlePrinterSelection = (event, printer) => {
    setSelectedPrinter(printer);
    setPrinterDetails(printer);
  };

  // Handle configuration change
  const handleConfigChange = (event) => {
    const { name, value, type, checked } = event.target;
    setConfig((prevConfig) => ({
      ...prevConfig,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  // Submit print request
  const handleSubmit = (event) => {
    event.preventDefault();

    if (!file || !selectedPrinter) {
      alert("Please upload a file and select a printer.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("printerId", selectedPrinter.printer_id);
    formData.append("config", JSON.stringify(config));

    fetch("http://localhost:3001/api/print", {
      method: "POST",
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to send print request");
        }
        return response.json();
      })
      .then((data) => {
        alert(`Print request submitted: ${data.message}`);
      })
      .catch((error) => console.error(error));
  };

  return (
    <div className="App">
      <h1>Print Request System</h1>

      {/* File Upload */}
      <div>
        <h2>Upload File</h2>
        <button onClick={() => document.getElementById("fileInput").click()}>
          Tải tệp tin mới
        </button>
        <input
          id="fileInput"
          type="file"
          style={{ display: "none" }}
          onChange={handleFileChange}
        />
        {file && <p>Selected File: {file.name}</p>}
      </div>

      {/* Confirmation Modal */}
      {showConfirmation && (
        <div className="modal">
          <div className="modal-content">
            <h2>Xác nhận</h2>
            <p>Bạn có chắc chắn muốn tải tệp "{file?.name}" lên không?</p>
            <button onClick={handleConfirmUpload}>Tải lên</button>
            <button onClick={handleCancelUpload}>Trở lại</button>
          </div>
        </div>
      )}

      {/* Configuration */}
      <div>
        <h2>Configure Print Settings</h2>
        <form>
          <label>
            Number of Copies:
            <input
              type="number"
              name="numCopies"
              value={config.numCopies}
              onChange={handleConfigChange}
              min="1"
            />
          </label>
          <br />
          <label>
            Page Size:
            <select
              name="pageSize"
              value={config.pageSize}
              onChange={handleConfigChange}
            >
              <option value="A4">A4</option>
              <option value="A3">A3</option>
              <option value="Letter">Letter</option>
            </select>
          </label>
          <br />
          <label>
            Orientation:
            <select
              name="isLandscape"
              value={config.isLandscape ? "Landscape" : "Portrait"}
              onChange={(e) =>
                setConfig((prevConfig) => ({
                  ...prevConfig,
                  isLandscape: e.target.value === "Landscape",
                }))
              }
            >
              <option value="Portrait">Portrait (Dọc)</option>
              <option value="Landscape">Landscape (Ngang)</option>
            </select>
          </label>
          <br />
          <label>
            Print Type:
            <select
              name="isDoubleSided"
              value={config.isDoubleSided ? "Double-Sided" : "Single-Sided"}
              onChange={(e) =>
                setConfig((prevConfig) => ({
                  ...prevConfig,
                  isDoubleSided: e.target.value === "Double-Sided",
                }))
              }
            >
              <option value="Single-Sided">Single-Sided (In 1 mặt)</option>
              <option value="Double-Sided">Double-Sided (In 2 mặt)</option>
            </select>
          </label>
        </form>
      </div>

      {/* Select Printer */}
      <div>
        <h2>Chọn máy in</h2>
        <button onClick={() => setShowPrinterList(!showPrinterList)}>
          Chọn máy in
        </button>
        {showPrinterList && (
          <div>
            <table border="1" style={{ width: "100%", marginTop: "10px" }}>
              <thead>
                <tr>
                  <th>Tên máy in</th>
                  <th>Hãng</th>
                  <th>Cơ sở</th>
                  <th>Vị trí</th>
                  <th>Trạng thái</th>
                </tr>
              </thead>
              <tbody>
                {printers.map((printer) => (
                  <tr key={printer.printer_id}>
                    <td>
                      <select
                        value={selectedPrinter.printer_model || ""}
                        onChange={(e) =>
                          handlePrinterSelection(e, printer)
                        }
                      >
                        <option value={printer.printer_model}>
                          {printer.printer_model}
                        </option>
                      </select>
                    </td>
                    <td>
                      <select
                        value={selectedPrinter.brand_name || ""}
                        onChange={(e) => handlePrinterSelection(e, printer)}
                      >
                        <option value={printer.brand_name}>
                          {printer.brand_name}
                        </option>
                      </select>
                    </td>
                    <td>
                      <select
                        value={selectedPrinter.campus_name || ""}
                        onChange={(e) => handlePrinterSelection(e, printer)}
                      >
                        <option value={printer.campus_name}>
                          {printer.campus_name}
                        </option>
                      </select>
                    </td>
                    <td>
                      <select
                        value={`${printer.building_name} - ${printer.room_number}`}
                        onChange={(e) => handlePrinterSelection(e, printer)}
                      >
                        <option value={`${printer.building_name} - ${printer.room_number}`}>
                          {`${printer.building_name} - ${printer.room_number}`}
                        </option>
                      </select>
                    </td>
                    <td>
                      <select
                        value={selectedPrinter.status || ""}
                        onChange={(e) => handlePrinterSelection(e, printer)}
                      >
                        <option value={printer.status}>{printer.status}</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Submit */}
      <div>
        <button onClick={handleSubmit}>Submit Print Request</button>
      </div>
    </div>
  );
}

export default App;
